import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Catalogue.css'; // Make sure this file exists and is correctly linked

const Catalogue = () => {
  const [activeSection, setActiveSection] = useState('');

  const handleSectionClick = (section) => {
    setActiveSection(activeSection === section ? '' : section); // Toggle section
  };

  const sectionsData = [
    {
      name: 'Men',
      image: '/images/men_section.jpg',
      subSections: [
        { name: 'Shirts', image: '/images/men_shirts.jpg' },
        { name: 'Pants', image: '/images/men_pants.jpg' },
        // Add more sub-sections as needed
      ],
    },
    {
      name: 'Women',
      image: '/images/women_section.jpg',
      subSections: [
        { name: 'Dresses', image: '/images/women_dresses.jpg' },
        { name: 'Skirts', image: '/images/women_skirts.jpg' },
        // Add more sub-sections as needed
      ],
    },
    {
      name: 'Kids',
      image: '/images/kids_section.jpg',
      subSections: [
        { name: 'Toys', image: '/images/kids_toys.jpg' },
        { name: 'Books', image: '/images/kids_books.jpg' },
        // Add more sub-sections as needed
      ],
    },
  ];

  return (
    <div className="catalogue-container">
      <h1>Catalogue</h1>
      <div className="catalogue-sections">
        <div onClick={() => handleSectionClick('Men')} style={menStyle} className="catalogue-section">Men</div>
        <div onClick={() => handleSectionClick('Women')} style={womenStyle} className="catalogue-section">Women</div>
        <div onClick={() => handleSectionClick('Kids')} style={kidsStyle} className="catalogue-section">Kids</div>
      </div>
      {activeSection === 'Men' && (
        <div className="sub-sections">
          {/* Example sub-section for Men */}
          <div className="catalogue-sub-section">Kandoora</div>
          <div className="catalogue-sub-section">Ghutra</div>
          <div className="catalogue-sub-section">Agal</div>
          <div className="catalogue-sub-section">Sandals</div>
          {/* Add more sub-sections as needed */}
        </div>
      )}
      {activeSection === 'Women' && (
        <div className="sub-sections">
          {/* Example sub-section for Women */}
          <div className="catalogue-sub-section">Abaya</div>
          <div className="catalogue-sub-section">Shayla</div>
          {/* Add more sub-sections as needed */}
        </div>
      )}
      {activeSection === 'Kids' && (
        <div className="sub-sections">
          {/* Example sub-section for Kids */}
          <div className="catalogue-sub-section">Kandoora</div>
          <div className="catalogue-sub-section">Ghutra</div>
          <div className="catalogue-sub-section">Sandals</div>
          {/* Add more sub-sections as needed */}
        </div>
      )}
    </div>
  );
};

export default Catalogue;
