import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Profile.css';

const Profile = () => {
  const navigate = useNavigate();

  // Initialize state with values from localStorage or empty strings
  const [username, setUsername] = useState(localStorage.getItem('username') || '');
  const [password, setPassword] = useState(localStorage.getItem('password') || ''); // Retrieve password from localStorage
  const [email, setEmail] = useState(localStorage.getItem('email') || '');
  const [phoneNumber, setPhoneNumber] = useState(localStorage.getItem('phoneNumber') || '');
  const [firstName, setFirstName] = useState(localStorage.getItem('firstName') || '');
  const [lastName, setLastName] = useState(localStorage.getItem('lastName') || '');

  const [editMode, setEditMode] = useState(false);
  const [editPassword, setEditPassword] = useState(false); // State to toggle password edit field

  const handleSignOut = () => {
    // Clear user information from localStorage
    localStorage.clear();
    // Redirect to the login page
    navigate('/login');
  };

  const handleProfileUpdate = async () => {
    const updatedInfo = {
      originalUsername: localStorage.getItem('username'), // Use the original username to identify the user in the backend
      newUsername: username,
      newPassword: password, // Include password in the update
      email,
      phoneNumber,
      firstName,
      lastName,
    };

    const response = await fetch('http://localhost:3000/updateProfile', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedInfo),
    });

    const data = await response.json();
    if (data.success) {
      // Update localStorage with new user info
      localStorage.setItem('username', username);
      localStorage.setItem('password', password); // Save the new password to localStorage
      localStorage.setItem('email', email);
      localStorage.setItem('phoneNumber', phoneNumber);
      localStorage.setItem('firstName', firstName);
      localStorage.setItem('lastName', lastName);

      alert('Profile updated successfully');
      setEditMode(false);
    } else {
      alert('Failed to update profile. ' + data.message);
    }
  };

  return (
    <div className="profile-container">
      <h1>User Profile</h1>
      {editMode ? (
        <>
          <label>Username:</label>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
          <label>Password:</label>
          {editPassword ? (
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          ) : (
            <span>******</span> // Placeholder for the password
          )}
          <button onClick={() => setEditPassword(!editPassword)}>
            {editPassword ? 'Cancel' : 'Change Password'}
          </button>
          <label>Email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <label>Phone Number:</label>
          <input type="text" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} />
          <label>First Name:</label>
          <input type="text" value={firstName} onChange={(e) => setFirstName(e.target.value)} />
          <label>Last Name:</label>
          <input type="text" value={lastName} onChange={(e) => setLastName(e.target.value)} />
          <button onClick={handleProfileUpdate} className="profile-save-button">Save</button>
          <button onClick={() => setEditMode(false)} className="cancel-edit-button">Cancel</button>
        </>
      ) : (
        <>
          <p><strong>Username:</strong> {username}</p>
          <p><strong>Password:</strong> {password}</p> {/* Displaying password in UI for demonstration */}
          <p><strong>Email:</strong> {email}</p>
          <p><strong>Phone Number:</strong> {phoneNumber}</p>
          <p><strong>First Name:</strong> {firstName}</p>
          <p><strong>Last Name:</strong> {lastName}</p>
          <button onClick={handleSignOut} className="profile-sign-out-button">Sign Out</button>
          <button onClick={() => setEditMode(true)} className="edit-profile-button">Edit Profile</button>
        </>
      )}
    </div>
  );
};

export default Profile;
