import React, { useState } from 'react';
import './SearchBar.css'; // Assuming you'll create a CSS file for styling

const SearchBar = ({ onSearch }) => {
    const [query, setQuery] = useState('');

    const handleInputChange = (e) => {
        setQuery(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSearch(query); // Use the onSearch prop to handle the search action
    };

    const clearQuery = () => {
        setQuery('');
        onSearch(''); // Clear the search and update the parent component
    };

    return (
        <form onSubmit={handleSubmit} className="search-bar">
            <input
                type="text"
                value={query}
                onChange={handleInputChange}
                placeholder="Search for items..."
                className="search-input"
            />
            {query && (
                <button type="button" onClick={clearQuery} className="clear-button">
                    X
                </button>
            )}
            <button type="submit" className="search-button">Search</button>
        </form>
    );
};

export default SearchBar;
