import React, { useState, useEffect } from 'react';
import './ProductList.css'; // Adjust the path according to your file structure

const ProductList = () => {
    const [products, setProducts] = useState([]); 
    const [selectedProduct, setSelectedProduct] = useState(null);

    // Fetch products from the server
    useEffect(() => {
        fetch('http://localhost:3000/products')
            .then(response => response.json())
            .then(data => {
                // Check if 'data' is an array before setting it to the state
                if (Array.isArray(data)) {
                    setProducts(data);
                } else if (Array.isArray(data.products)) {
                    // If 'data' contains a 'products' field that is an array, use that instead
                    setProducts(data.products);
                } else {
                    // Handle cases where 'data' is not in the expected format
                    console.error("Fetched data is not an array:", data);
                    setProducts([]); // Set products to an empty array to avoid runtime errors
                }
            })
            .catch(error => {
                console.error("Error fetching products:", error);
                setProducts([]); // Set products to an empty array in case of fetch error
            });
    }, []);

    // Function to add an item to the cart
    const addItemToCart = async (item) => {
        const username = localStorage.getItem("username"); // Retrieve the username from localStorage
        const response = await fetch('http://localhost:3000/addToCart', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ username, item }),
        });
        const data = await response.json();
        if (data.success) {
            alert('Item added to cart successfully');
        } else {
            alert('Failed to add item to cart');
        }
    };

    const renderProductDetails = (product) => {
        return (
            <div className="product-details">
                <h2>{product.name} - ${product.price}</h2>
                <p>{product.description}</p>
                <p>Quantity: {product.quantity}</p> {/* Adjust based on your data structure */}
                <button 
                    className="add-to-cart-button" 
                    onClick={() => addItemToCart({
                        id: product.id,
                        title: product.name,
                        price: product.price,
                        quantity: 1 // Assuming a default quantity
                    })}
                >
                    Add to Cart
                </button>
                <button className="back-button" onClick={() => setSelectedProduct(null)}>Back to Products</button>
            </div>
        );
    };

};

export default ProductList;
