import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
  const [userData, setUserData] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      // Example: Get the current username from local storage
      const username = localStorage.getItem('username');
      if (!username) {
        navigate('/login');
        return;
      }

      try {
        const response = await fetch(`http://localhost:3000/user/${username}`);
        if (!response.ok) {
          throw new Error('User data fetch failed');
        }
        const data = await response.json();
        setUserData(data);
      } catch (error) {
        console.error("Error fetching user data:", error);
        // Handle error (e.g., redirect to login)
      }
    };

    fetchUserData();
  }, [navigate]);

  return (
    <div className="profile-container">
      <h1>Profile</h1>
      {/* Display user data */}
      <p>Email: {userData.email || 'N/A'}</p>
      <p>Username: {userData.username || 'N/A'}</p>
      <p>Phone Number: {userData.phoneNumber || 'N/A'}</p>
      <p>First Name: {userData.firstName || 'N/A'}</p>
      <p>Last Name: {userData.lastName || 'N/A'}</p>
      {/* Add any additional user data you wish to display */}
    </div>
  );
};

export default Profile;
