import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Cart.css';

const Cart = () => {
    const [cartItems, setCartItems] = useState([]);

    useEffect(() => {
        // Retrieve the username from localStorage
        const username = localStorage.getItem("username");

        if (username) {
            fetch(`http://localhost:3000/cart/${username}`)
                .then(response => response.json())
                .then(data => {
                    if (data.success && data.items) {
                        setCartItems(data.items); // Make sure to update the state with the fetched items
                    } else {
                        alert('Failed to fetch cart items');
                    }
                })
                .catch(error => {
                    console.error("Error fetching cart items:", error);
                    alert('Failed to fetch cart items');
                });
        } else {
            console.log("Username not found in localStorage. User might not be logged in.");
        }
    }, []); // The empty dependency array ensures this effect runs only once after the initial render

    return (
        <div className="cart-container">
            <h1>Your Cart</h1>
            <div className="cart-items">
                {cartItems.length > 0 ? (
                    cartItems.map((item, index) => (
                        <div key={index} className="cart-item">
                            <h2>{item.title}</h2>
                            <p>Price: ${item.price}</p>
                            <p>Quantity: {item.quantity}</p>
                            {/* Add more details as needed */}
                        </div>
                    ))
                ) : (
                    <p>Your cart is empty.</p>
                )}
            </div>
            <div className="cart-actions">
                <Link to="/" className="cart-button">Back to Home</Link>
                <Link to="/payment" className="cart-button">Proceed to Checkout</Link>
            </div>
        </div>
    );
};

export default Cart;
