import React from 'react';
import { Link } from 'react-router-dom';
import SearchBar from './SearchBar'; // Import the SearchBar component
import './Home.css';

const Home = ({ isAdmin }) => { // Assume isAdmin is passed as a prop for simplicity
  const handleSearch = (query) => {
    console.log("Search for:", query);
    // Here you will handle the search operation
  };

  return (
    <div className="home-container">
      <SearchBar onSearch={handleSearch} /> {/* Include the SearchBar at the top */}
      <h1>Welcome to Our Clothing Store</h1>
      <div className="home-buttons">
        <Link to="/cart" className="home-button">View Cart</Link>
        <Link to="/profile" className="home-button">Profile</Link>
        <Link to="/catalogue" className="home-button">Catalogue</Link>
        {isAdmin && (
          <>
            <Link to="/admin/products" className="home-button">Manage Products</Link>
            <Link to="/admin/users" className="home-button">Manage Users</Link>
            <Link to="/admin/orders" className="home-button">Manage Orders</Link>
            <Link to="/admin/analytics" className="home-button">View Analytics</Link>
          </>
        )}
      </div>
    </div>
  );
};

export default Home;
