import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Orders.css';

const Orders = () => {
    const [orders, setOrders] = useState([]);
    const [productNames, setProductNames] = useState({});

    useEffect(() => {
        // Fetch and parse product details
        const fetchProductDetails = async () => {
            try {
                const response = await fetch('http://localhost:3000/products');
                const text = await response.text();
                const products = text.trim().split('\n\n').reduce((acc, productText) => {
                    const lines = productText.split('\n');
                    const [id, name] = lines[0].split('\t');
                    acc[id] = name;
                    return acc;
                }, {});
                setProductNames(products);
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };

        // Fetch orders
        const fetchOrders = async () => {
            const username = localStorage.getItem("username");
            if (username) {
                try {
                    const ordersResponse = await fetch(`http://localhost:3000/orders/${username}`);
                    const ordersData = await ordersResponse.json();
                    if (ordersData.orders && Array.isArray(ordersData.orders)) {
                        setOrders(ordersData.orders);
                    } else {
                        console.error("Unexpected response format:", ordersData);
                    }
                } catch (error) {
                    console.error("Error fetching orders:", error);
                }
            }
        };

        fetchProductDetails().then(fetchOrders);
    }, []);

    return (
        <div className="orders-container">
            <h1>Your Orders</h1>
            {orders.length > 0 ? (
                orders.map((order, index) => (
                    <div key={index} className="order-item">
                        <h2>Order ID: {order.orderID}</h2>
                        <p>Order Date: {order.orderDate}</p>
                        <p>Status: {order.orderStatus}</p>
                        <p>Products:</p>
                        <ul>
                            {order.products.map((product, productIndex) => (
                                <li key={productIndex}>
                                    <Link to={`/products?search=${product.id}`}>
                                        {productNames[product.id] || 'Unknown Product'} ({product.id})
                                    </Link>
                                </li>
                            ))}
                        </ul>
                    </div>
                ))
            ) : (
                <p>No orders found.</p>
            )}
        </div>
    );
};

export default Orders;
