import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Cart.css';

const Cart = () => {
    const [cartItems, setCartItems] = useState([]);

    useEffect(() => {
        fetchCartItems();
    }, []);

    const fetchCartItems = async () => {
        const username = localStorage.getItem("username");
        if (username) {
            try {
                const response = await fetch(`http://localhost:3000/cart/${username}`);
                const data = await response.json();
                setCartItems(data);
            } catch (error) {
                console.error("Error fetching cart items:", error);
            }
        }
    };

    const updateQuantityOnServer = async (itemId, action) => {
        const username = localStorage.getItem("username");
        const endpoint = `http://localhost:3000/cart/${action}`;
        try {
            await fetch(endpoint, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, itemId }),
            });
            fetchCartItems(); // Re-fetch cart items to reflect the updated quantity
        } catch (error) {
            console.error("Error updating cart item quantity on server:", error);
        }
    };

    const incrementQuantity = (itemId) => {
        updateQuantityOnServer(itemId, 'increment');
    };

    const decrementQuantity = (itemId) => {
        updateQuantityOnServer(itemId, 'decrement');
    };

    return (
        <div className="cart-container">
            <h1>Your Cart</h1>
            <div className="cart-items">
                {cartItems.length > 0 ? (
                    cartItems.map((item, index) => (
                        <div key={index} className="cart-item">
                            <h2>{item.title}</h2>
                            <p>Price: ${item.price}</p>
                            <div className="quantity-controls">
                                <button onClick={() => decrementQuantity(item.id)}>-</button>
                                <p>{item.quantity}</p>
                                <button onClick={() => incrementQuantity(item.id)}>+</button>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>Your cart is empty.</p>
                )}
            </div>
            <div className="cart-actions">
                <Link to="/" className="cart-button">Back to Home</Link>
                <Link to="/payment" className="cart-button">Proceed to Checkout</Link>
            </div>
        </div>
    );
};

export default Cart;
