const express = require('express');
const fs = require('fs');
const path = require('path');
const router = express.Router();

// Helper function to read user data
const getUserDataByUsername = (username) => {
  const filePath = path.join(__dirname, 'data', 'users.txt');
  const users = fs.readFileSync(filePath, 'utf8').trim().split('\n');
  const userData = users.find((line) => line.split('\t')[0] === username);
  if (userData) {
    const [username, password, email, phoneNumber, firstName, lastName] = userData.split('\t');
    return { username, email, phoneNumber, firstName, lastName };
  }
  return null;
};

// Endpoint to get user data by username
router.get('/user/:username', (req, res) => {
  const { username } = req.params;
  const userData = getUserDataByUsername(username);
  if (userData) {
    res.json(userData);
  } else {
    res.status(404).send('User not found');
  }
});

// Make sure to use the router in your express app
// app.use(router);

module.exports = router;
