import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import SearchBar from './SearchBar'; // Ensure this component allows input and can trigger searches
import './Home.css';

const Home = () => {
  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:3000/products');
        const data = await response.json();
        if (data.success) {
          setProducts(data.products);
          setFilteredProducts(data.products); // Initially, no search query, so show all products
        }
      } catch (error) {
        console.error("Failed to fetch products", error);
      }
    };

    fetchProducts();
  }, []);

  useEffect(() => {
    // Filter products based on the search query
    const filtered = products.filter(product =>
      product.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredProducts(filtered);
  }, [searchQuery, products]);

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  return (
    <div className="home-container">
      <SearchBar onSearch={handleSearch} />
      <h1>Welcome to Our Clothing Store</h1>
      <div>
        {filteredProducts.map((product, index) => (
          <div key={index}>
            <p>{product.name} - ${product.price}</p>
          </div>
        ))}
      </div>
      <div className="home-buttons">
        <Link to="/cart" className="home-button">View Cart</Link>
        <Link to="/profile" className="home-button">Profile</Link>
        <Link to="/catalogue" className="home-button">Catalogue</Link>
      </div>
    </div>
  );
};

export default Home;
