// src/components/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAuth, signInWithEmailAndPassword } from "firebase/auth";
import { getDatabase, ref, child, get } from "firebase/database";
import { auth } from '../firebase-config'; // Adjust the path as necessary

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const dbRef = ref(getDatabase());
    // Assuming usernames are stored under a 'usernames' node
    get(child(dbRef, `usernames/${username}`)).then((snapshot) => {
      if (snapshot.exists()) {
        const userEmail = snapshot.val().email; // Adjust based on your database structure
        signInWithEmailAndPassword(auth, userEmail, password)
          .then((userCredential) => {
            navigate('/profile'); // Navigate to the profile page
          })
          .catch((error) => {
            console.error("Authentication error:", error);
            alert("Authentication failed: " + error.message);
          });
      } else {
        alert("Username does not exist.");
      }
    }).catch((error) => {
      console.error("Database read error:", error);
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={username} onChange={e => setUsername(e.target.value)} placeholder="Username" />
      <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" />
      <button type="submit">Login</button>
    </form>
  );
};

export default Login;
