// ProductList.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import SearchBar from './SearchBar'; // Adjust the import path as necessary
import './ProductList.css'; // Adjust the import path as necessary

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const navigate = useNavigate();
    const location = useLocation();

    useEffect(() => {
        // Fetch products when the component mounts or the location changes
        const fetchProducts = async () => {
            const response = await fetch('http://localhost:3000/products');
            const data = await response.json();
            setProducts(data.products || []);
            setFilteredProducts(data.products || []);
        };

        fetchProducts();
    }, [location]); // Dependency on location ensures re-fetching when query params change

    const handleSearch = (query) => {
        if (!query.trim()) {
            setFilteredProducts(products);
            return;
        }
        const lowerCaseQuery = query.toLowerCase();
        const filtered = products.filter(product =>
            product.id.toLowerCase().includes(lowerCaseQuery) ||
            product.name.toLowerCase().includes(lowerCaseQuery) ||
            product.description.toLowerCase().includes(lowerCaseQuery)
        );
        setFilteredProducts(filtered);
    };

    return (
        <div className="product-list-container">
            <div className="search-bar-container">
                <button 
                    className="back-to-home-button" 
                    onClick={() => navigate(-1)}
                >
                    Back
                </button>
                <div className="search-bar-wrapper">
                    <SearchBar onSearch={handleSearch} />
                </div>
            </div>
            <div className="products-grid">
                {filteredProducts.map((product, index) => (
                    <div key={index} className="product-item">
                        <img src={`/images/${product.id}.jpg`} alt={product.name} className="product-image" />
                        <h2 className="product-title">{product.name} - {product.price} AED</h2>
                        <p>{product.description}</p>
                        {/* Implement functionality for these buttons as needed */}
                        <button className="view-details-button">View Details</button>
                        <button className="add-to-cart-button">Add to Cart</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ProductList;
