// src/components/Orders.js
import React, { useState, useEffect } from 'react';
import './Orders.css';

const Orders = () => {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        const fetchOrders = async () => {
            const username = localStorage.getItem("username");
            if (username) {
                try {
                    const response = await fetch(`http://localhost:3000/orders/${username}`);
                    const data = await response.json();
                    // Check if data.orders exists and is an array before setting it
                    if (data.orders && Array.isArray(data.orders)) {
                        setOrders(data.orders);
                    } else {
                        // Handle the case where data.orders is not as expected
                        console.error("Unexpected response format:", data);
                        setOrders([]); // Ensure orders is always an array
                    }
                } catch (error) {
                    console.error("Error fetching orders:", error);
                    setOrders([]); // Ensure orders is always an array
                }
            }
        };

        fetchOrders();
    }, []);

    return (
        <div className="orders-container">
            <h1>Your Orders</h1>
            {orders.length > 0 ? (
                orders.map((order, index) => (
                    <div key={index} className="order-item">
                        <h2>Order ID: {order.orderID}</h2>
                        <p>{order.username} - {order.orderDate}</p>
                        <p>Products: {order.productIDs.join(", ")}</p>
                        <p>Status: {order.orderStatus}</p>
                    </div>
                ))
            ) : (
                <p>No orders found.</p>
            )}
        </div>
    );
};

export default Orders;
