import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });
      const data = await response.json();
      if (data.success) {
        if (data.isAdmin) {
          // Navigate to the admin page if the user is an admin
          navigate('/admin');
        } else {
          // Navigate to the home page for regular users
          navigate('/');
        }
      } else {
        alert(data.message);
      }
    } catch (error) {
      alert("An error occurred during login.");
    }
  };

  return (
    <div className="login-container">
      <h1>Login</h1>
      <form onSubmit={handleSubmit} className="login-form">
        <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" required className="login-input" />
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required className="login-input" />
        <button type="submit" className="login-button">Login</button>
      </form>
      <div className="login-links">
        <Link to="/signup" className="login-link">Don't have an account? Sign Up</Link>
      </div>
    </div>
  );
};

export default Login;
