import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import SearchBar from './SearchBar'; // Import the SearchBar component
import './Home.css';

const Home = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  useEffect(() => {
      const fetchProducts = async () => {
          // Fetch your products from the server or local data
          const response = await fetch('http://your-api/products');
          const data = await response.json();
          setProducts(data);
          setFilteredProducts(data);
      };

      fetchProducts();
  }, []);

  useEffect(() => {
      const filtered = products.filter(product =>
          product.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredProducts(filtered);
  }, [searchQuery, products]);

  return (
    <div className="home-container">
        <input
            type="text"
            placeholder="Search products..."
            value={searchQuery}
            onChange={handleSearchChange}
        />
        <h1>Welcome to Our Clothing Store</h1>
        <div className="home-buttons">
            <Link to="/cart" className="home-button">View Cart</Link>
            <Link to="/profile" className="home-button">Profile</Link>
            <Link to="/catalogue" className="home-button">Catalogue</Link>
        </div>
        {/* Display filtered products */}
        <div className="products">
            {filteredProducts.map(product => (
                <div key={product.id}>
                    <h2>{product.name}</h2>
                    {/* Other product details */}
                </div>
            ))}
        </div>
    </div>
);
};

export default Home;
