import React from 'react';
import { Link } from 'react-router-dom';
import './Catalogue.css'; // Create and import your CSS file for styling

const Catalogue = () => {
  const menStyle = {
    backgroundImage: 'url(/images/kandoora.jpg)',
    backgroundSize: 'cover',
    backgroundPosition: 'contian',
    height: '300px', // Adjust based on your design needs
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    color: 'white', // Adjust text color based on your image
  };

  const womenStyle = {
    backgroundImage: 'url(/images/abaya.jpg)',
    backgroundSize: 'contain',
    backgroundPosition: 'center',
    height: '300px', // Adjust based on your design needs
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    color: 'white', // Adjust text color based on your image
  };

  return (
    <div className="catalogue-container">
      <h1>Catalogue</h1>
      <div className="catalogue-sections">
        <Link to="/catalogue/men" style={menStyle} className="catalogue-section">
          Men
        </Link>
        <Link to="/catalogue/women" style={womenStyle} className="catalogue-section">
          Women
        </Link>
      </div>
    </div>
  );
};

export default Catalogue;
