import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Orders.css';

const Orders = () => {
    const [orders, setOrders] = useState([]);
    const [products, setProducts] = useState([]);

    useEffect(() => {
        console.log('Fetching products...');
        // Fetch products once and store them
        const fetchProducts = async () => {
            try {
                const response = await fetch(`http://localhost:3000/products`);
                const data = await response.json();
                setProducts(data.products); // Assuming the response has a 'products' key
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };

        fetchProducts();
    }, []);

    useEffect(() => {
        console.log('Products loaded:', products);
        const fetchOrders = async () => {
            console.log('Fetching orders for:', localStorage.getItem("username"));
            const username = localStorage.getItem("username");
            if (username) {
                try {
                    const response = await fetch(`http://localhost:3000/orders/${username}`);
                    const data = await response.json();
                    if (data.orders && Array.isArray(data.orders)) {
                        // Enhance orders with product names
                        const enhancedOrders = data.orders.map(order => ({
                            ...order,
                            products: order.products.map(productId => {
                                const product = products.find(p => p.id === productId);
                                return { id: productId, name: product ? product.name : 'Unknown Product' };
                            })
                        }));
                        setOrders(enhancedOrders);
                    } else {
                        console.error("Unexpected response format:", data);
                        setOrders([]);
                    }
                } catch (error) {
                    console.error("Error fetching orders:", error);
                    setOrders([]);
                }
            }
        };

        if (products.length > 0) {
            fetchOrders();
        } else {
            console.log('Waiting for products to load...');
        }
    }, [products]);

    return (
        <div className="orders-container">
            <h1>Your Orders</h1>
            {orders.length > 0 ? (
                orders.map((order, index) => (
                    <div key={index} className="order-item">
                        <h2>Order ID: {order.orderID}</h2>
                        <p>Order Date: {order.orderDate}</p>
                        <p>Status: {order.orderStatus}</p>
                        <p>Products:</p>
                        <ul>
                            {order.products.map(product => (
                                <li key={product.id}>
                                    <Link to={`/products?search=${product.id}`}>{product.name} ({product.id})</Link>
                                </li>
                            ))}
                        </ul>
                    </div>
                ))
            ) : (
                <p>No orders found.</p>
            )}
        </div>
    );
};

export default Orders;
