import React, { useState, useEffect } from 'react';
import './ProductList.css'; // Adjust the path as necessary

const ProductList = () => {
    const [products, setProducts] = useState([]); // Initialize products state
    const [selectedProduct, setSelectedProduct] = useState(null);

    // Fetch products from the server
    useEffect(() => {
        fetch('http://localhost:3000/products')
            .then(response => response.json())
            .then(data => {
                setProducts(data); // Assuming the data is the array of products
            })
            .catch(error => {
                console.error("Error fetching products:", error);
            });
    }, []);

    // Function to add an item to the cart (assuming this part is already implemented)

    // Function to render product details
    const renderProductDetails = (product) => {
        // Implementation remains the same
    };

    return (
        <div className="product-list-container">
            {selectedProduct ? (
                renderProductDetails(selectedProduct)
            ) : (
                <>
                    <h1>Products</h1>
                    <div>
                        {products.map((product, index) => (
                            <div key={index} className="product-item">
                                <h2 className="product-title">{product.name} - ${product.price}</h2>
                                <p>{product.description}</p>
                                <button 
                                    className="view-details-button" 
                                    onClick={() => setSelectedProduct(product)}
                                >
                                    View Details
                                </button>
                                <button 
                                    className="add-to-cart-button" 
                                    onClick={() => addItemToCart({
                                        id: product.id,
                                        title: product.name,
                                        price: product.price,
                                        quantity: 1
                                    })}
                                >
                                    Add to Cart
                                </button>
                            </div>
                        ))}
                    </div>
                </>
            )}
        </div>
    );
};

export default ProductList;
