import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import SearchBar from './SearchBar'; // Import the SearchBar component if you're using it separately

const Home = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);

    useEffect(() => {
        // Example fetch function to get products. Adjust the URL as needed.
        const fetchProducts = async () => {
            const response = await fetch('http://your-api/products');
            const data = await response.json();
            setProducts(data);
            setFilteredProducts(data); // Initially, filteredProducts is the same as all products
        };

        fetchProducts();
    }, []);

    useEffect(() => {
        // This effect runs whenever searchQuery or products changes.
        const filtered = products.filter(product =>
            product.name.toLowerCase().includes(searchQuery.toLowerCase())
            // You can add more conditions to filter by other product attributes if needed
        );
        setFilteredProducts(filtered);
    }, [searchQuery, products]);

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    return (
        <div className="home-container">
            <input
                type="text"
                placeholder="Search products..."
                value={searchQuery}
                onChange={handleSearchChange}
            />
            <h1>Welcome to Our Clothing Store</h1>
            <div className="home-buttons">
                <Link to="/cart" className="home-button">View Cart</Link>
                <Link to="/profile" className="home-button">Profile</Link>
                <Link to="/catalogue" className="home-button">Catalogue</Link>
            </div>
            {/* Display filtered products */}
            <div className="products">
                {filteredProducts.map(product => (
                    <div key={product.id}>
                        <h2>{product.name}</h2>
                        {/* Other product details */}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
