import React, { useState, useEffect } from 'react';
import './ProductList.css'; // Adjust the path according to your file structure

const ProductList = () => {
    const [selectedProduct, setSelectedProduct] = useState(null);

    // Fetch products from the server
    useEffect(() => {
        fetch('http://localhost:3000/products')
            .then(response => response.json())
            .then(data => {
                setProducts(data); // Assuming the data is the array of products
            })
            .catch(error => {
                console.error("Error fetching products:", error);
            });
    }, []);

    // Function to add an item to the cart
    const addItemToCart = async (item) => {
        const username = localStorage.getItem("username"); // Retrieve the username from localStorage
        const response = await fetch('http://localhost:3000/addToCart', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ username, item }),
        });
        const data = await response.json();
        if (data.success) {
            alert('Item added to cart successfully');
        } else {
            alert('Failed to add item to cart');
        }
    };

    const renderProductDetails = (product) => {
        return (
            <div className="product-details">
                <h2>{product.name} - ${product.price}</h2>
                <p>{product.description}</p>
                <p>Quantity: {product.quantity}</p> {/* Adjust based on your data structure */}
                <button 
                    className="add-to-cart-button" 
                    onClick={() => addItemToCart({
                        id: product.id,
                        title: product.name,
                        price: product.price,
                        quantity: 1 // Assuming a default quantity
                    })}
                >
                    Add to Cart
                </button>
                <button className="back-button" onClick={() => setSelectedProduct(null)}>Back to Products</button>
            </div>
        );
    };

    return (
        <div className="product-list-container">
            {selectedProduct ? (
                renderProductDetails(selectedProduct)
            ) : (
                <>
                    <h1>Products</h1>
                    <div>
                        {products.map((product, index) => (
                            <div key={index} className="product-item">
                                <h2 className="product-title">{product.name} - ${product.price}</h2>
                                <p>{product.description}</p>
                                <button 
                                    className="view-details-button" 
                                    onClick={() => setSelectedProduct(product)}
                                >
                                    View Details
                                </button>
                                <button 
                                    className="add-to-cart-button" 
                                    onClick={() => addItemToCart({
                                        id: product.id,
                                        title: product.name,
                                        price: product.price,
                                        quantity: 1
                                    })}
                                >
                                    Add to Cart
                                </button>
                            </div>
                        ))}
                    </div>
                </>
            )}
        </div>
    );
};

export default ProductList;
