// Profile.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Profile.css';

const Profile = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user')); // Assuming user details are stored in local storage

  const handleLogout = () => {
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('user'); // Clear user details
    navigate('/login');
  };

  return (
    <div className="profile-container">
      <h1>Your Profile</h1>
      <div className="profile-details">
        <p>Username: {user?.username}</p> {/* Display username dynamically */}
        <p>Email: {user?.email}</p> {/* Display email dynamically */}
      </div>
      <button onClick={handleLogout}>Sign Out</button>
    </div>
  );
};

export default Profile;
