// Profile.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Profile.css';

const Profile = () => {
  const navigate = useNavigate();

  // Safely retrieve and parse the user details from local storage
  const userString = localStorage.getItem('user');
  // Ensure userString is not null or 'undefined' before parsing
  const user = userString && userString !== 'undefined' ? JSON.parse(userString) : null;

  const handleLogout = () => {
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('user'); // Clear user details
    navigate('/login');
  };

  return (
    <div className="profile-container">
      <h1>Your Profile</h1>
      <div className="profile-details">
        {/* Display username and email dynamically, with fallbacks if not available */}
        <p>Username: {user ? user.username : 'N/A'}</p>
        <p>Email: {user ? user.email : 'N/A'}</p>
      </div>
      <button onClick={handleLogout}>Sign Out</button>
    </div>
  );
};

export default Profile;
