import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAuth, onAuthStateChanged, signOut } from "firebase/auth";
import { auth } from '../firebase-config';
import './Home.css'; // Assuming you're using Home.css for styling

const Profile = () => {
  const [userData, setUserData] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    onAuthStateChanged(auth, (user) => {
      if (user) {
        // Here, you might want to fetch and set user data from your database
        setUserData({ email: user.email }); // Example: setting email
      } else {
        navigate('/login');
      }
    });
  }, [navigate]);

  const handleSignOut = async () => {
    await signOut(auth);
    navigate('/login');
  };

  return (
    <div className="home-container">
      <h1>Profile</h1>
      <div className="home-form">
        <p>Email: {userData.email || 'N/A'}</p>
        <p>Email: {userData.username || 'N/A'}</p>
        <p>Email: {userData.firstname || 'N/A'}</p>
        <p>Email: {userData.lastname || 'N/A'}</p>
        {/* Display other user details */}
      </div>
      <div className="home-buttons">
        <button onClick={() => navigate(-1)} className="home-button">Back</button>
        <button onClick={handleSignOut} className="home-button">Sign Out</button>
      </div>
    </div>
  );
};

export default Profile;
