import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import SearchBar from './SearchBar'; // Ensure this path matches your file structure
import './ProductList.css';

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [displayProducts, setDisplayProducts] = useState([]); // Use for rendering in the UI
    const [selectedProduct, setSelectedProduct] = useState(null);
    const navigate = useNavigate();
    const location = useLocation();

    useEffect(() => {
        const fetchData = async () => {
            const queryParams = new URLSearchParams(location.search);
            const idPrefix = queryParams.get('id');
            try {
                const response = await fetch('http://localhost:3000/products');
                const data = await response.json();
                let fetchedProducts = Array.isArray(data) ? data : data.products || [];
                if (idPrefix) {
                    fetchedProducts = fetchedProducts.filter(product => product.id.startsWith(idPrefix));
                }
                setProducts(fetchedProducts);
                setDisplayProducts(fetchedProducts);
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };
        fetchData();
    }, [location]);

    const handleSearch = (query) => {
        if (!query.trim()) {
            setDisplayProducts(products);
            return;
        }
        const lowerCaseQuery = query.toLowerCase();
        const filtered = products.filter(product =>
            product.id.toLowerCase().includes(lowerCaseQuery) ||
            product.name.toLowerCase().includes(lowerCaseQuery) ||
            product.description.toLowerCase().includes(lowerCaseQuery)
        );
        setDisplayProducts(filtered);
    };

    const addItemToCart = async (product) => {
        const username = localStorage.getItem("username");
        const response = await fetch('http://localhost:3000/addToCart', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ username, item: product }),
        });
        const data = await response.json();
        if (data.success) {
            alert('Item added to cart successfully');
        } else {
            alert('Failed to add item to cart');
        }
    };

    return (
        <div className="product-list-container">
            <div className="search-bar-container">
                <button className="back-to-home-button" onClick={() => navigate(-1)}>Back</button>
                <SearchBar onSearch={handleSearch} />
            </div>
            {selectedProduct ? (
                <div className="product-details">
                    {/* Product details view */}
                    <h2>{selectedProduct.name} - {selectedProduct.price} AED</h2>
                    <p>{selectedProduct.description}</p>
                    <button 
                        className="add-to-cart-button" 
                        onClick={() => addItemToCart(selectedProduct)}
                    >
                        Add to Cart
                    </button>
                    <button className="back-button" onClick={() => setSelectedProduct(null)}>Back to Products</button>
                </div>
            ) : (
                <>
                    <h1>Products</h1>
                    <div className="products-grid">
                        {displayProducts.map((product, index) => (
                            <div key={index} className="product-item">
                                <img src={`/images/${product.id}.jpg`} alt={product.name} className="product-image" />
                                <h2 className="product-title">{product.name} - {product.price} AED</h2>
                                <p>{product.description}</p>
                                <button 
                                    className="view-details-button" 
                                    onClick={() => setSelectedProduct(product)}
                                >
                                    View Details
                                </button>
                                <button 
                                    className="add-to-cart-button" 
                                    onClick={() => addItemToCart(product)}
                                >
                                    Add to Cart
                                </button>
                            </div>
                        ))}
                    </div>
                </>
            )}
        </div>
    );
};

export default ProductList;
