import React, { useState, useEffect } from 'react';
import { getAuth, onAuthStateChanged, signOut } from "firebase/auth";
import { getDatabase, ref, get } from "firebase/database";
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import { auth } from '../firebase-config';
import './Home.css'; // Use the same CSS file for styling

const Profile = () => {
 const [userData, setUserData] = useState({});
 const auth = getAuth();
 const database = getDatabase();
 const navigate = useNavigate(); // Initialize useNavigate

 useEffect(() => {
    onAuthStateChanged(auth, (user) => {
      if (user) {
        const uid = user.uid;
        get(ref(database, 'users/' + uid)).then((snapshot) => {
          if (snapshot.exists()) {
            setUserData(snapshot.val());
          } else {
            console.log("No data available");
          }
        }).catch((error) => {
          console.error(error);
        });
      }
    });
 }, []);

 const handleSignOut = async () => {
    try {
      await signOut(auth);
      navigate('/login'); // Redirect to the login page after signing out
    } catch (error) {
      console.error(error);
    }
 };

 return (
    <div className="home-container">
      <h1>Profile</h1>
      <div className="home-form">
        <p>Email: {userData.email || 'N/A'}</p>
        <p>Username: {userData.username || 'N/A'}</p>
        <p>Phone Number: {userData.phone || 'N/A'}</p>
        <p>First Name: {userData.firstName || 'N/A'}</p>
        <p>Last Name: {userData.lastName || 'N/A'}</p>
      </div>
      <div className="home-buttons">
        <button className="home-button" onClick={() => navigate(-1)}>Back</button>
        <button className="home-button" onClick={handleSignOut}>Sign Out</button>
      </div>
    </div>
 );
};

export default Profile;
