import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './Signup.css';

const Signup = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  // Add other state variables as necessary (e.g., username, firstName)
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/signup', { // Adjust the endpoint as necessary
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password /*, other fields */ }),
      });
      const data = await response.json();
      if (data.success) {
        alert("User created successfully!");
        navigate('/login'); // Redirect to the login page
      } else {
        alert(data.message);
      }
    } catch (error) {
      alert("An error occurred during signup.");
    }
  };

  return (
    <div className="signup-container">
      <h1>Sign Up</h1>
      <form onSubmit={handleSubmit} className="signup-form">
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required className="signup-input" placeholder="Email" />
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required className="signup-input" placeholder="Password" />
        {/* Include other input fields as necessary */}
        <button type="submit" className="signup-button">Sign Up</button>
      </form>
      <Link to="/login" className="back-to-login-link">Back to Login</Link>
    </div>
  );
};

export default Signup;
