// AdminDashboard.js
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './AdminDashboard.css'; // Make sure you have this CSS file created based on the previous instructions

const AdminDashboard = () => {
    const navigate = useNavigate();

    const handleNavigation = (path) => {
        navigate(path);
      };
      
    const handleSignOut = () => {
        // Clear user session from local storage or any state management you are using
        localStorage.clear();
        // Redirect to the login page or home page
        navigate('/login');
    };

    return (
        <div className="admin-dashboard-container">
        <h1>Admin Dashboard</h1>
        <div className="admin-buttons">
            <button onClick={() => handleNavigation('/admin/products')} className="admin-button">Manage Products</button>
            <button onClick={() => handleNavigation('/admin/users')} className="admin-button">Manage Users</button>
            <button onClick={() => handleNavigation('/admin/orders')} className="admin-button">Manage Orders</button>
            <button onClick={() => handleNavigation('/admin/analytics')} className="admin-button">View Analytics</button>
            <button onClick={handleSignOut} className="sign-out-button">Sign Out</button>
        </div>
        </div>
    );
};

export default AdminDashboard;
