import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Cart.css';

const Cart = () => {
    const [cartItems, setCartItems] = useState([]);

    // Replace 'currentUsername' with the actual logic to retrieve the current user's username
    const username = 'currentUsername';

    useEffect(() => {
        fetch(`http://localhost:3000/cart/${username}`)
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    setCartItems(data.items);
                } else {
                    alert('Failed to fetch cart items');
                }
            })
            .catch(error => {
                console.error("Error fetching cart items:", error);
                alert('Failed to fetch cart items');
            });
    }, [username]); // Dependency array to re-fetch when username changes

    return (
        <div className="cart-container">
            <h1>Your Cart</h1>
            <div className="cart-items">
                {cartItems.length > 0 ? (
                    cartItems.map((item, index) => (
                        <div key={index} className="cart-item">
                            {/* Placeholder for item image */}
                            <div className="cart-item-details">
                                <h2>{item.title}</h2>
                                <p>Price: ${item.price}</p>
                                <p>Quantity: {item.quantity}</p>
                                {/* Add more details as needed */}
                            </div>
                        </div>
                    ))
                ) : (
                    <p>Your cart is empty.</p>
                )}
            </div>
            <div className="cart-actions">
                <Link to="/" className="cart-button">Back to Home</Link>
                <Link to="/payment" className="cart-button">Proceed to Checkout</Link>
            </div>
        </div>
    );
};

export default Cart;
