// ForgotPassword.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ResetPasswordAndForgotPassword.css';

const ForgotPassword = () => {
    const [email, setEmail] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
        const response = await fetch('http://localhost:3000/forgot-password', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ email }),
        });
        const data = await response.json();
        if (data.success) {
            alert('Password reset link has been sent to your email.');
            navigate('/login');
        } else {
            alert(data.message);
        }
        } catch (error) {
        console.error("An error occurred during forgot password:", error);
        alert("An error occurred during forgot password.");
        }
    };

    const handleBack = () => {
        navigate('/login'); // Navigates to the previous page
        // navigate('/login'); // Use this instead if you want to navigate to a specific route
    };

    return (
        <div className="forgot-password-container">
        <h1>Forgot Password</h1>
        <form onSubmit={handleSubmit} className="forgot-password-form">
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required className="forgot-password-input" />
            <button type="submit" className="forgot-password-button">Send Reset Link</button>
        </form>
        </div>
    );
};

export default ForgotPassword;
