import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Orders.css';

const Orders = () => {
    const [orders, setOrders] = useState([]);
    const [productNames, setProductNames] = useState({});

    useEffect(() => {
        const fetchProductNames = async () => {
            try {
                const response = await fetch('http://localhost:3000/products');
                const text = await response.text();
                const lines = text.split('\n');
                const names = {};
                for (let i = 0; i < lines.length; i += 2) {
                    const details = lines[i].split('\t'); // Split the first line of each product entry
                    if (details.length >= 2) { // Ensure there are at least two details (ID and name)
                        const id = details[0];
                        const name = details[1];
                        names[id] = name;
                    }
                }
                console.log("Fetched product names:", names); // Debugging log
                return names;
            } catch (error) {
                console.error("Error fetching product names:", error);
                return {};
            }
        };

        const fetchOrdersAndProductNames = async () => {
            const username = localStorage.getItem("username");
            if (username) {
                try {
                    const response = await fetch(`http://localhost:3000/orders/${username}`);
                    const data = await response.json();
                    if (data.orders && Array.isArray(data.orders)) {
                        setOrders(data.orders);
                    } else {
                        console.error("Unexpected response format:", data);
                        setOrders([]);
                    }
                } catch (error) {
                    console.error("Error fetching orders:", error);
                    setOrders([]);
                }
            }

            const names = await fetchProductNames();
            setProductNames(names);
        };

        fetchOrdersAndProductNames();
    }, []);

    return (
        <div className="orders-container">
            <h1>Your Orders</h1>
            {orders.length > 0 ? (
                orders.map((order, index) => (
                    <div key={index} className="order-item">
                        <h2>Order ID: {order.orderID}</h2>
                        <p>Order Date: {order.orderDate}</p>
                        <p>Status: {order.orderStatus}</p>
                        <p>Products:</p>
                        <ul>
                            {order.products.map(product => (
                                <li key={product.id}>
                                    <Link to={`/products?search=${product.id}`}>
                                        {productNames[product.id] || 'Unknown Product'} ({product.id})
                                    </Link>
                                </li>
                            ))}
                        </ul>
                    </div>
                ))
            ) : (
                <p>No orders found.</p>
            )}
        </div>
    );
};

export default Orders;
